      name: "location",
      title: "Location",
      type: "string",
      validation: Rule => Rule.required(),
      fieldset: "information"
    },
    {
      name: "adoptionFee",
      title: "Adoption Fee",
      type: "string",
      validation: Rule => Rule.required(),
      fieldset: "information"
