    {
      name: "homeAlone",
      title: "Can Be Left Alone?",
      type: "boolean",
      fieldset: "stats"
    },

    // -- Main Post --
    {
      name: "mainImage",
      title: "Image",
      type: "image",
      validation: Rule => Rule.required(),
      options: {
        hotspot: true,
      },
      fieldset: "images"
    },
    {
      title: 'Image Alternate Text',
      name: 'alt',
      type: 'string',
      validation: Rule => Rule.required(),
      fieldset: "images"
    },
    {
      name: "publishedAt",
      title: "Published At",
      type: "date",
      validation: Rule => Rule.required()
    },
    {
      name: "body",
      title: "Description",
      type: "blockContent",
      validation: Rule => Rule.required()
    },
  ],

  preview: {
    select: {
      title: "name",
      subtitle: "breed",
      media: "mainImage",
      // TO DO - is there a way to prepare publishedAt date format with moment in the preview?
    }
  }
};

